import { sql } from "drizzle-orm";
import { pgTable, text, varchar, timestamp, boolean, integer, jsonb } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  username: text("username").notNull().unique(),
  password: text("password").notNull(),
  role: text("role").notNull(), // 'student', 'instructor', 'admin', 'coordinator'
  firstName: text("first_name").notNull(),
  lastName: text("last_name").notNull(),
  createdAt: timestamp("created_at").defaultNow(),
});

export const sessions = pgTable("sessions", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  name: text("name").notNull(),
  instructorId: varchar("instructor_id").references(() => users.id).notNull(),
  scenarioId: varchar("scenario_id").references(() => scenarios.id),
  active: boolean("active").default(true),
  timeRemaining: integer("time_remaining"), // in minutes
  createdAt: timestamp("created_at").defaultNow(),
});

export const sessionParticipants = pgTable("session_participants", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  sessionId: varchar("session_id").references(() => sessions.id).notNull(),
  userId: varchar("user_id").references(() => users.id).notNull(),
  joinedAt: timestamp("joined_at").defaultNow(),
});

export const scenarios = pgTable("scenarios", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  name: text("name").notNull(),
  description: text("description"),
  createdBy: varchar("created_by").references(() => users.id).notNull(),
  timeline: jsonb("timeline"), // Array of timed events
  createdAt: timestamp("created_at").defaultNow(),
});

export const patients = pgTable("patients", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  mrn: text("mrn").notNull().unique(),
  firstName: text("first_name").notNull(),
  lastName: text("last_name").notNull(),
  dateOfBirth: text("date_of_birth").notNull(),
  gender: text("gender").notNull(),
  location: text("location"),
  status: text("status").notNull(), // 'critical', 'stable', 'monitoring'
  chiefComplaint: text("chief_complaint"),
  sessionId: varchar("session_id").references(() => sessions.id),
  createdAt: timestamp("created_at").defaultNow(),
});

export const medicalHistory = pgTable("medical_history", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  patientId: varchar("patient_id").references(() => patients.id).notNull(),
  condition: text("condition").notNull(),
  diagnosedYear: text("diagnosed_year"),
  notes: text("notes"),
});

export const medications = pgTable("medications", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  patientId: varchar("patient_id").references(() => patients.id).notNull(),
  name: text("name").notNull(),
  dosage: text("dosage").notNull(),
  frequency: text("frequency").notNull(),
  prescribedBy: varchar("prescribed_by").references(() => users.id),
  prescribedAt: timestamp("prescribed_at").defaultNow(),
});

export const vitalSigns = pgTable("vital_signs", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  patientId: varchar("patient_id").references(() => patients.id).notNull(),
  bloodPressure: text("blood_pressure"),
  heartRate: integer("heart_rate"),
  respiratoryRate: integer("respiratory_rate"),
  temperature: text("temperature"),
  oxygenSaturation: integer("oxygen_saturation"),
  recordedAt: timestamp("recorded_at").defaultNow(),
  recordedBy: varchar("recorded_by").references(() => users.id),
});

export const labResults = pgTable("lab_results", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  patientId: varchar("patient_id").references(() => patients.id).notNull(),
  testName: text("test_name").notNull(),
  value: text("value").notNull(),
  unit: text("unit"),
  referenceRange: text("reference_range"),
  status: text("status").default('completed'), // 'pending', 'completed'
  orderedAt: timestamp("ordered_at").defaultNow(),
  completedAt: timestamp("completed_at"),
  orderedBy: varchar("ordered_by").references(() => users.id),
});

export const soapNotes = pgTable("soap_notes", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  patientId: varchar("patient_id").references(() => patients.id).notNull(),
  subjective: text("subjective"),
  objective: text("objective"),
  assessment: text("assessment"),
  plan: text("plan"),
  authorId: varchar("author_id").references(() => users.id).notNull(),
  createdAt: timestamp("created_at").defaultNow(),
});

export const orders = pgTable("orders", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  patientId: varchar("patient_id").references(() => patients.id).notNull(),
  type: text("type").notNull(), // 'lab', 'imaging', 'medication'
  orderText: text("order_text").notNull(),
  status: text("status").default('pending'), // 'pending', 'completed', 'cancelled'
  orderedBy: varchar("ordered_by").references(() => users.id).notNull(),
  orderedAt: timestamp("ordered_at").defaultNow(),
  completedAt: timestamp("completed_at"),
});

export const groups = pgTable("groups", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  name: text("name").notNull(),
  description: text("description"),
  sessionId: varchar("session_id").references(() => sessions.id).notNull(),
  createdAt: timestamp("created_at").defaultNow(),
});

export const groupMembers = pgTable("group_members", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  groupId: varchar("group_id").references(() => groups.id).notNull(),
  userId: varchar("user_id").references(() => users.id).notNull(),
  joinedAt: timestamp("joined_at").defaultNow(),
});

export const assets = pgTable("assets", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  filename: text("filename").notNull(),
  type: text("type").notNull(), // 'pdf', 'image', 'lab', 'document'
  filePath: text("file_path").notNull(),
  sessionId: varchar("session_id").references(() => sessions.id).notNull(),
  uploadedBy: varchar("uploaded_by").references(() => users.id).notNull(),
  uploadedAt: timestamp("uploaded_at").defaultNow(),
});

export const assetGroupVisibility = pgTable("asset_group_visibility", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  assetId: varchar("asset_id").references(() => assets.id).notNull(),
  groupId: varchar("group_id").references(() => groups.id).notNull(),
  visible: boolean("visible").default(false),
  changedBy: varchar("changed_by").references(() => users.id).notNull(),
  changedAt: timestamp("changed_at").defaultNow(),
});

// Data Versioning for Groups
export const dataVersions = pgTable("data_versions", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  name: text("name").notNull(),
  description: text("description"),
  version: text("version").notNull(),
  sessionId: varchar("session_id").references(() => sessions.id).notNull(),
  createdBy: varchar("created_by").references(() => users.id).notNull(),
  createdAt: timestamp("created_at").defaultNow(),
});

// Group-specific patient assignments and data versions
export const groupDataAssignments = pgTable("group_data_assignments", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  groupId: varchar("group_id").references(() => groups.id).notNull(),
  dataVersionId: varchar("data_version_id").references(() => dataVersions.id).notNull(),
  patientId: varchar("patient_id").references(() => patients.id).notNull(),
  assignedBy: varchar("assigned_by").references(() => users.id).notNull(),
  assignedAt: timestamp("assigned_at").defaultNow(),
});

// Group account credentials managed by System Admin
export const groupAccounts = pgTable("group_accounts", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  groupId: varchar("group_id").references(() => groups.id).notNull(),
  username: text("username").notNull().unique(),
  password: text("password").notNull(),
  active: boolean("active").default(true),
  createdBy: varchar("created_by").references(() => users.id).notNull(),
  createdAt: timestamp("created_at").defaultNow(),
});

// Documents uploaded and managed by Simulation Coordinators
export const documents = pgTable("documents", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  filename: text("filename").notNull(),
  originalName: text("original_name").notNull(),
  fileType: text("file_type").notNull(),
  fileSize: integer("file_size").notNull(),
  filePath: text("file_path").notNull(),
  category: text("category").notNull(), // 'admission', 'lab', 'imaging', 'notes'
  patientId: varchar("patient_id").references(() => patients.id),
  sessionId: varchar("session_id").references(() => sessions.id).notNull(),
  uploadedBy: varchar("uploaded_by").references(() => users.id).notNull(),
  uploadedAt: timestamp("uploaded_at").defaultNow(),
});

// Scheduled document releases controlled by Simulation Coordinators
export const documentReleases = pgTable("document_releases", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  documentId: varchar("document_id").references(() => documents.id).notNull(),
  groupId: varchar("group_id").references(() => groups.id).notNull(),
  releaseType: text("release_type").notNull(), // 'manual', 'scheduled'
  scheduledAt: timestamp("scheduled_at"),
  releasedAt: timestamp("released_at"),
  releasedBy: varchar("released_by").references(() => users.id),
  status: text("status").default('pending'), // 'pending', 'released', 'cancelled'
  notes: text("notes"),
});

// Simulation week configuration for timeline control
export const simulationWeeks = pgTable("simulation_weeks", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  name: text("name").notNull(),
  weekNumber: integer("week_number").notNull(),
  startDate: timestamp("start_date").notNull(),
  endDate: timestamp("end_date").notNull(),
  sessionId: varchar("session_id").references(() => sessions.id).notNull(),
  timeline: jsonb("timeline"), // Array of timed events and document releases
  active: boolean("active").default(false),
  createdBy: varchar("created_by").references(() => users.id).notNull(),
  createdAt: timestamp("created_at").defaultNow(),
});

// Activity audit trail for System Admin oversight
export const auditLog = pgTable("audit_log", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  action: text("action").notNull(),
  entityType: text("entity_type").notNull(), // 'user', 'group', 'document', 'patient'
  entityId: varchar("entity_id").notNull(),
  performedBy: varchar("performed_by").references(() => users.id).notNull(),
  details: jsonb("details"),
  timestamp: timestamp("timestamp").defaultNow(),
});

// Insert schemas
export const insertUserSchema = createInsertSchema(users).omit({
  id: true,
  createdAt: true,
});

export const insertSessionSchema = createInsertSchema(sessions).omit({
  id: true,
  createdAt: true,
});

export const insertPatientSchema = createInsertSchema(patients).omit({
  id: true,
  createdAt: true,
