export async function groupAccessMiddleware(req: Request, res: Response, next: NextFunction) {
  try {
    // ä»Žä¼šè¯ä¸­èŽ·å–ç”¨æˆ·ä¿¡æ¯
    const userId = req.session?.user?.id;

