    const requestedGroupId = req.params.groupId || req.query.groupId as string;
    if (requestedGroupId) {
      if (!req.userGroups.includes(requestedGroupId)) {
        return res.status(403).json({ 
          message: "ç¦æ­¢è®¿é—®ï¼šæ‚¨ä¸å±žäºŽè¯·æ±‚çš„ç»„" 
